function v=PsychToolboxVersion% PsychToolboxVersion returns a number indicating the version. global PsychToolboxif ~isfield(PsychToolbox,'version')	% Get version and date of PsychToolbox from PsychToolbox:Contents.m	PsychToolbox.version=0;	file=[matlabroot ':toolbox:PsychToolbox:Contents.m'];	f=fopen(file,'r');	fgetl(f);	s=fgetl(f);	fclose(f);	[PsychToolbox.version,count,errmsg,n]=sscanf(s,'%% Version %f',1);	ss=s(n:end);	PsychToolbox.date=ss(min(find(ss-' ')):end);endv=PsychToolbox.version;