function DescribeMonCal(cal,file)% DescribeMonCal(cal,file)% % Print descriptive information about a calibration % to the command window or file.%% Argument file is a standard MATLAB file descriptor,% see fopen.  If file arg is omitted, printout% goes to command window.%% 8/25/97  dhb, pbe  Wrote it.% 7/3/98   dhb, pbe  Updated for cal.describe.% 12/3/99  dhb, mpr  Fix check for calibration desription field.if (nargin == 1)	file = 1;endif (~isfield(cal,'describe'))	fprintf(file,'Calibration structure has no description\n');endif (isfield(cal.describe,'computer'))	fprintf(file,'Computer: %s\n',cal.describe.computer);else	fprintf(file,'No information on computer\n');endif (isfield(cal.describe,'whichScreen'))	fprintf(file,'Screen: %g\n',cal.describe.whichScreen);else	fprintf(file,'No information on screen\n');endif (isfield(cal.describe,'monitor'))	fprintf(file,'Monitor: %s\n',cal.describe.monitor);else	fprintf(file,'No information on monitor\n');endif (isfield(cal.describe,'driver'))	fprintf(file,'Video driver: %s\n',cal.describe.driver);else	fprintf(file,'No information on video driver\n');endif (isfield(cal.describe,'who'))	fprintf(file,'Calibration performed by %s\n',cal.describe.who);else	fprintf(file,'No specification of who did calibration\n');endif (isfield(cal.describe,'date'))	fprintf(file,'Calibration performed on %s\n',cal.describe.date);else	fprintf(file,'No date specified for calibration\n');endif (isfield(cal.describe,'program'))	fprintf(file,'Calibration program: %s\n',cal.describe.program);else	fprintf(file,'No calibration program specified');endif (isfield(cal.describe,'comment'))	fprintf(file,'Comment: %s\n',cal.describe.comment);else	fprintf(file,'No additional descriptive information available\n');endfprintf(file,'Calibrated device has %g primaries\n',cal.nDevices);fprintf(file,'\n');