function GammaFlipDemo% GammaFlipDemo shows the effects of flipping the CLUT and flipping the% gamma table. Tries 8 and 10 bit gamma tables.% 2/16/00 dgp Wrote it, in response to query by Harriet Allen.G=[0:255]';GT8=[G G G];G=[0:4:1023]';GT10=[G G G];for s=SCREEN('Screens')	window=SCREEN(s,'OpenWindow');	screen(window,'TextSize',18);	LUT=ClutDefault(window);	SCREEN(window,'SetClut',LUT);	Ask(window,'Normal CLUT. Click to continue.');	SCREEN(window,'SetClut',flipud(LUT));	Ask(window,'Flipped CLUT. Click to continue.');	[old,bits,err]=SCREEN(window,'Gamma',GT8,8);	if ~err.set		SCREEN(window,'SetClut',LUT);		Ask(window,'Normal 8-bit gamma. Click to continue.');	end	[old,bits,err]=SCREEN(window,'Gamma',flipud(GT8),8);	if ~err.set		SCREEN(window,'SetClut',LUT);		Ask(window,'Flipped 8-bit gamma. Click to continue.');	end	[old,bits,err]=SCREEN(window,'Gamma',GT10,10);	if ~err.set		SCREEN(window,'SetClut',LUT);		Ask(window,'Normal 10-bit gamma. Click to continue.');	end	[old,bits,err]=SCREEN(window,'Gamma',flipud(GT10),10);	if ~err.set		SCREEN(window,'SetClut',LUT);		Ask(window,'Flipped 10-bit gamma. Click to continue.');	end	SCREEN(window,'Close');end