% LoadTheImagesPD%% Subscript that holds the code for loading the images% for the PhaseDistort demo.%% Since this is a script, it uses the same workspace as the% main program.%% 3/5/97  dhb  Pulled out of main script.targetName = 'Reagan';imageName1 = 'reagan128';imageName2 = 'einstein128';if (exist(imageName1) ~= 1)	fprintf('Loading %s from disk\n',imageName1);	eval(['load ' imageName1 ';']);	eval(['image1 = ' imageName1 ';']);	image1Fft= fft2(image1);	image1Amp= abs(image1Fft);	image1Phase = angle(image1Fft);endif (exist(imageName2) ~= 1)	fprintf('Loading %s from disk\n',imageName2);	eval(['load ' imageName2 ';']);	eval(['image2 = ' imageName2 ';']);	image2Fft= fft2(image2);	image2Amp= abs(image2Fft);	image2Phase = angle(image2Fft);end