function output = ComputeGammaExpP(x,input)% output = ComputeGammaExpP(x,input)%% Compute the gamma table using an extended power function.%% tmp1 = x(1).^(1/x(2));% output = HalfRect( ( x(3) + tmp1*input).^x(2) );%% 9/21/93  dhb,ccc  Added checks for parameter bounds.% 10/3/93  dhb,jms  Normalize output to max of 1.%                   Better be sure that last value is max setting.if (x(2) <= 0)  [m,n] = size(input);  output = ones(m,n);elseif (x(1) <= 0)  [m,n] = size(input);  output = HalfRect( (x(3)^x(2))*ones(m,n) );   else  if (x(3) <= 0)    x(3) = 0;  end  tmp1 = x(1).^(1/x(2));  output = HalfRect( ( x(3) + tmp1*input).^x(2) );endoutput = NormalizeGamma(output);