function SetColor(windowPtrOrScreenNumber,color,rgb,bits)% SetColor(windowPtrOrScreenNumber,color,rgb,[bits])% % OBSOLETE: Use SCREEN 'SetClut'.% % Set the specified entry of the clut to the passed rgb column vector.% "color", specifying which entry, should be in the range 0:255.%% 10/29/93  dhb  Wrote it.% 5/23/96   dhb  Add optional windowPtr variable.% 3/29/97   dgp  Updated.% 4/12/97   dhb  Bits arg added.% 2/19/98   dgp  Removed comment that implied that windowPtrOrScreenNumber is optional.% Check argsif nargin < 3 | nargin > 4 | nargout > 0	error('Usage: SetColor(windowPtrOrScreenNumber,color,rgb,[bits])');endif length(windowPtrOrScreenNumber) ~= 1 	error('Usage: SetColor(windowPtrOrScreenNumber,color,rgb,[bits]))');end% Set defaultsdefaultBits = 8;% Check args and set defaultsif (nargin < 4)	bits = defaultBits;elseif isempty(bits)	bits = defaultBits;endSCREEN(windowPtrOrScreenNumber,'SetClut',rgb',color,bits);