function gammaTable=GammaIdentity(windowPtrOrScreenNumber)% gammaTable=GammaIdentity(windowPtrOrScreenNumber)%% This provides an identity gamma table appropriate to the screen's dacSize.%% 7/21/98  dgp  Wrote it.% 8/26/98  dgp  Changed it to use same length as table obtained from driver.% 8/31/98  dgp  Fixed fatal bug introduced 8/26.if nargin~=1 | nargout>1	error('Usage:  gammaTable=GammaIdentity(windowPtrOrScreenNumber)');enddacSize=8;[gammaTable,dacSize,gammaError]=SCREEN(windowPtrOrScreenNumber,'Gamma');if ~exist('dacSize') | isempty(dacSize)	dacSize=8;endgammaLength=size(gammaTable,1);clear gammaTablegammaTable=[0:(gammaLength-1)]'*(2^dacSize-1)/(gammaLength-1);