function ColorTest% ColorTest exercises all the SCREEN functions that accept a "color"% argument, showing that they work consistently at all pixelSizes. % Note that specification of RGB color is only allowed when pixelSize % is 16 or 32.% % Denis Pelli, April 6, 1998% 4/1/98 dgp Wrote it.% 4/6/98 dgp Wait 2 s instead of waiting for click.screens=SCREEN('Screens');whichScreen=screens(end);pix=SCREEN(whichScreen,'PixelSizes');for pixelSize=pix	w=screen(whichScreen,'OpenWindow',[],[],pixelSize);	ww=screen(w,'OpenOffscreenWindow');	white=WhiteIndex(w);	black=BlackIndex(w);	gray=round((white+black)/2);	if gray==white		gray=black;	end	SCREEN(w,'DrawLine',0.4*black+0.6*white,0,0,1000,1000,10,10);	SCREEN(w,'FrameRect',0.3*black+0.7*white,[],10,10);	SCREEN(w,'FrameOval',0.1*black+0.9*white,[],10,10);	SCREEN(w,'FrameArc',0.2*black+0.8*white,[],0,90,10,10);	SCREEN(w,'FillRect',black,[200 200 600 600]);	SCREEN(w,'FillOval',gray,[600 200 1000 600]);	SCREEN(w,'FillPoly',white,[200 400;400 200;1000 400;400 600;200 400]);	SCREEN(w,'TextSize',36);	string=sprintf('pixelSize %d',pixelSize);	if pixelSize>8		color=[white,black,black];	% red		string=[string ', RGB color'];	else		color=gray;		string=[string ', grayscale'];	end	screen(w,'DrawText',string,250,412,color);	SCREEN(w,'TextSize',48);	WaitSecs(1.5);	screen('CloseAll');endhelp ColorTest