function HelpTest(directory)% HelpTest([directory]) asks for help on every file and folder% within the specified folder (and subfolders). The default% is the current directory, i.e. CD.%% Denis Pelli 19 July 1998if nargin>1	error('Usage: HelpTest([directory])');endoldDirectory=cd;if nargin==1	cd(directory);else	directory=cd;endhelp(directory);dd=dir(directory);for i=1:length(dd)	if dd(i).isdir		HelpTest(dd(i).name);	% folder	else		help(dd(i).name);		% file	endendcd(oldDirectory);return;