function SaveAsEpsTest% SaveAsEpsTest checks out SCREEN SaveAsEps, which saves an image to disk.cd(matlabroot);cd ..;cd ..;window=screen(0,'openwindow',[],offsetrect([0 0 20 10],0,650),8);rect=SCREEN(window,'Rect');oldFontSize=SCREEN(window,'TextSize',10);screen(window,'PutImage',0:255,rect);SCREEN(window,'DrawText','Hi',0,10,BlackIndex(window));delete test.epsSCREEN(window,'SaveAsEps','test.eps');% paperRect=SetRect(0,11*72,8.5*72,0);% pageRect=rect.*[1,-1,1,-1];% convert from Apple to Adobe coordinates% pageRect=CenterRect(pageRect,paperRect);% SCREEN(window,'SaveAsEps','test.eps',rect,pageRect)SCREEN(window,'Close');