function [m,v] = binostat(n,p)% BINOSTAT Mean and variance of the binomial distribution.%   [M, V] = BINOSTAT(N,P) returns the mean and variance of the%   binomial distribution with parameters N and P.if nargin < 2,     error('Requires two input arguments.'); end[errorcode n p] = distchck(2,n,p);if errorcode > 0    error('Requires non-scalar arguments to match in size.');endm = n .* p;v = n .* p .* (1 - p);% Return NaN for parameter values outside their respective limits.k = find(p<0 | p>1 | n<0 | round(n)~=n);if any(k)    tmp = NaN;    m(k) = tmp(ones(size(k)));    v(k) = m(k);end