function [B] = MakeFourierBasis(wls,nbasis);% [B] = MakeFourierBasis(wls,nbasis);%% Make a fourier basis set.%% 5/17/94		dhb		Added this comment.% Get wavelength sampling info[n,m] = size(wls);lowwl = wls(1);highwl = wls(n);wlrange = highwl-lowwl;freqarg = ( wls - lowwl*ones(n,1) ) / (wlrange);% Make DCB(:,1) = 0.5*ones(n,1);% Make cos/sin pairsk = 1;while ( k <= nbasis )  k = k+1;  B(:,k) = cos( 2*pi*(k-1)*freqarg );  k = k+1;  B(:,k) = sin( 2*pi*(k-2)*freqarg );end% Truncate basis set to desired sizeB = B(:,1:nbasis);