function S = WlsToS(wls)% S = WlsToS(wls)%% Converts a list of wavelengths to a [start delta num]% description.  Wavelengths must be evenly spaced.[m,n] = size(wls);if (n ~= 1)  error('Passed wavelengths is not a column vector');endS = zeros(1,3);S(1) = wls(1);S(2) = wls(2)-wls(1);S(3) = m;chk_wls = SToWls(S);if ( abs(wls-chk_wls) > 1e-10)  error('Passed wavelengths are not evenly space');end