function y=movementinc(timer);global mdata;global mvmntcount;global memdata;global bighspec;global bigtspec;global bigvspec;%if (pulse==-1000) % start simulation%  load mdata;%  memdata=mdata;%  memdata%  mdata=0;%  mdata(1,1:3)=memdata(1,:);%  makeinput;%  [x,y,z]=euler('isi2network3',1,[],[1e-5,1e-3,1e-3,[],[],[]]);%end;if ((timer==0))% & (mvmntcount<size(memdata,1)))  memdata  if ((mvmntcount<size(memdata,1)))    disp(sprintf('movementinc: setting up the next movement (counter=%.0g) . . .',mvmntcount));    mdata(1,1:3)=memdata(mvmntcount+1,:);    disp(mdata);    makeinput;  else    bighspec(:)=zeros(size(bighspec));    bigvspec(:)=zeros(size(bigvspec));    bigtspec(:)=zeros(size(bigtspec));    end;  end;y=0;