function[y] = SBN(x)% nonlinearities used in the simulation of pure saccades% input = conjugate motor error% output = firing rate of the conjugate saccade burst neurons (SBN)%define parameterse0=5;AL=530;AR=530;LL=10;LR=10;% figure(2);% hold off;  % for x=-10:10if x>=e0	y=AR*(1.0-exp(-(e0+x)/LR));endif -e0<x	if x<e0    	y=AR*(1.0-exp(-(e0+x)/LR))-AL*(1.0-exp(-(e0-x)/LL));	endendif x<=-e0        y=-(AL*(1.0-exp(-(e0-x)/LL)));end% plot(x,y,'o');% hold on;% set(gca,'xlim',[-10,10]);% title('SBN nonlinearity');% xlabel('conjugate motor error');% ylabel('conjugate saccade burst');% x=x+1;% end;% hold off;