function[y] = SVBN(x)% nonlinearities used in the simulation of vergence combined with saccades% input = vergence motor error% output = firing rate of the saccade-related vergence burst neurons (SVBN)% define parameters separated into divergence and convergence,% designated by values with an appended D or CAC=60;AD=80;lC=6;lD=4;% figure(2);% hold off;% for x=-10:10if x>=0			%convergence	y=AC*(1.0-exp(-x/lC));%       plot(x,y,'o');elseif  x<0		%divergence	y=AD*(exp(x/lD)-1.0);%       plot(x,y,'o');%       hold on;end% set(gca,'xlim',[-10,10]);% set(gca,'ylim',[-400,400]);% title('SVBN nonlinearity');% xlabel('vergence motor error');% ylabel('saccade-related vergence burst');% end% hold off;