function[y] = VVN(x)% nonlinearities used in the simulation of pure vergence% input = vergence motor error% output = firing rate of the vergence velocity neurons (VVN)% define parameters separated into divergence and convergence,% designated by values with an appended D or CnC=1.0;nD=0.8;kC=120;kD=125;tC=15;tD=30;% figure(2);% hold off;% for x=-10:10if x>=0			%convergence	y=(kC*x^nC)/((x^nC)+(tC^nC));%       plot(x,y,'o');%       hold on;elseif x<0		%divergence	y=real((kD*x^nD)/((x^nD)+(tD^nD)));%       plot(x,y,'o');end% set(gca,'xlim',[-10,10]);% set(gca,'ylim',[-400,400]);% title('VVN nonlinearity');% xlabel('vergence motor error');% ylabel('pure vergence output');% end% hold off;