function [y,s_rate]=play_spikes(spike_train,nreps,s_rate)%[y,s_rate]=play_spikes(spike_train,[nreps],[s_rate])%%Plays a spike train through the speaker. %%Inputs:   spike_train      vector of spike times (msec)%          nreps            number of repetitons of auditory sequence.%                           keyboard hit stops repetitions.%                           default is 1.  %          s_rate           sampling rate for MATLAB sound function.%                           default is 1 KHz.%%Output    Sequence can be played with 'sound(y,s_rate)'%7/8/96    gmb			Wrote it.%12/13/96 mns disabled kbhit on sgi   if nargin<3  s_rate=1;  %KHzends_rate=min(s_rate,22.255);if nargin<2  nreps=1;endbins=0:max(spike_train)*s_rate;[y,~]=hist(spike_train*s_rate,bins);for i=1:nreps  sound(y,s_rate*1000);  % $$$ 	if (kbhit)  % $$$ 		return  % $$$ 	endend